    clips.forEach(clip => api.actions[clip.name] = mixer.clipAction(clip, currentRoot));
    return () => Object.values(api.actions).forEach(action => mixer.uncacheAction(action, currentRoot));
  }, [api, clips, mixer, root, actualRef]);
